$(".agregarOpcion").click(function(){contFormulario=$("#modalAgregarOpcion");pTexto=contFormulario.find('input[name="texto"]').val();if(pTexto==""){return}HTMLOpcion=$("#HTMLOpcion").html();$(".Opciones").append(HTMLOpcion);nuevaOpcion=$(".Opciones").children().last();nuevaOpcion.find(".texto").html(pTexto);nuevaOpcion.find('input[name="textoOpcion[]"]').val(pTexto);nuevaOpcion.find(".subirOpcion").click(function(){Contenedor=$(this).parentsUntil("li.Opcion").parent();Contenedor.prev().before(Contenedor);return false});nuevaOpcion.find(".bajarOpcion").click(function(){Contenedor=$(this).parentsUntil("li.Opcion").parent();Contenedor.next().after(Contenedor);return false});nuevaOpcion.find(".eliminarOpcion").click(function(){Contenedor=$(this).parentsUntil("li.Opcion").parent();Contenedor.hide("fast",function(){$(this).remove()});return false});$("#modalAgregarOpcion").modal("hide");contFormulario.find('input[name="texto"]').val("")});